    public static function seminars()
    {
        $seminars = DataManager::rawQuery("SELECT COUNT(id) AS count,date  AS day,session FROM seminar_days GROUP BY date,session ORDER BY date,session ASC");

        $sems = [];
        foreach ($seminars as $seminar){
            $sems[$seminar->day][$seminar->session]["count"] = $seminar->count;
            $sems[$seminar->day][$seminar->session]["participants"] = self::participantsPerDay($seminar->day,$seminar->session);
        }

        return $sems;
    }

    public static function participantsPerDay($day,$session)
    {
        $dm = DataManager::query("seminar_days","application_id",["date"=>$day, "session"=>$session]);
        foreach ($dm as $d){
            $ids[] = $d->application_id;
        }
        $application_ids = implode(',',$ids);
        $participants = DataManager::query("participants","COUNT(id) AS count","application_id IN ($application_ids)");
        return empty($participants)?0:$participants[0]->count;
    }

