    public static function reports($where="",$erx=0)
    {
        $_where = "";
        $_join = $erx?"LEFT ":"";
        if($where!="")$_where="WHERE $where";
        switch ($_SESSION['usr']->user_group){
            case "Quebec": $id = 10693;break;
            case "London": $id = 10400;break;
            case "Calgary": $id = 10107;break;
            case "vancouver": $id = 46894;break;
            case "All":$id = 0;
        }
        if($id){
            $_where = $_where?$_where." AND company.city='$id' ":" WHERE company.city='$id' ";
        }
        return DataManager::rawQuery("SELECT company.id,company.name,countries.name AS country,cities.name AS city,states.name AS province,company.dealer_code,
            GROUP_CONCAT(DISTINCT CONCAT(attendies.firstname,':',attendies.lastname ,':',attendies.email,':',attendies.mobile) ORDER BY attendies.id DESC SEPARATOR '~ ') AS attendees,
            GROUP_CONCAT(DISTINCT CONCAT(contact_persons.firstname, ':',contact_persons.lastname,':',contact_persons.email,':',contact_persons.phone) ORDER BY contact_persons.id DESC SEPARATOR '~ ') AS contact_persons
            FROM company
            JOIN contact_persons ON company.id=contact_persons.company_id 
            $_join JOIN registration ON registration.company_id=company.id 
            LEFT JOIN attendies ON attendies.registration_id=registration.id 
            LEFT JOIN cities ON company.city=cities.id 
            LEFT JOIN states ON company.province=states.id 
            LEFT JOIN countries ON company.country=countries.id 
            $_where
            GROUP BY company.id ");

    }

    public static function mostOf($companies=[])
    {
        $_whereC = "";
        if(!empty($companies)){
            $c = implode(",",$companies);
            $_whereC = "WHERE company_id IN ($c)";
        }
        $attendees = DataManager::rawQuery("SELECT COUNT(attendies.id) AS count FROM attendies JOIN registration ON registration.id=attendies.registration_id JOIN company ON company.id = registration.company_id $_whereC GROUP BY registration_id ORDER BY count DESC");
        $contact_persons = DataManager::rawQuery("SELECT COUNT(id) AS count FROM contact_persons $_whereC GROUP BY company_id ORDER BY count DESC");

        $attendees = count($attendees)?$attendees[0]->count:0;
        $contact_persons = count($contact_persons)?$contact_persons[0]->count:0;

        return ["attendees"=>$attendees,"contact_persons"=>$contact_persons];
    }

    public static function totals()
    {
        return DataManager::rawQuery("SELECT 
                application.plan AS plan,
                IF(application.plan='A',2000,IF(application.plan='B',1000,500)) AS planCost,
                (SELECT COUNT(id) FROM presenter WHERE presenter.application_id=application.id) as presenters,
                (SELECT COUNT(id) FROM seminar_days WHERE seminar_days.application_id=application.id) as seminar_days,
                (SELECT COUNT(id) FROM participants WHERE participants.application_id=application.id) AS participants,
                IF(application.brand_recognition='yes',50,0) AS brand_recognition,
                (application.promotional_flyer*50) as promotional_flyer
                
                FROM vendors 
                JOIN application ON application.vendor_id=vendors.id 
                WHERE 1 
                GROUP BY vendors.id");
    }

    public static function total($id)
    {
        return DataManager::rawQuery("SELECT 
                application.plan AS plan,
                IF(application.plan='A',2000,IF(application.plan='B',1000,500)) AS planCost,
                (SELECT COUNT(id) FROM presenter WHERE presenter.application_id=application.id) as presenters,
                (SELECT COUNT(id) FROM seminar_days WHERE seminar_days.application_id=application.id) as seminar_days,
                (SELECT COUNT(id) FROM participants WHERE participants.application_id=application.id) AS participants,
                IF(application.brand_recognition='yes',50,0) AS brand_recognition,
                (application.promotional_flyer*50) as promotional_flyer
                
                FROM vendors 
                JOIN application ON application.vendor_id=vendors.id 
                WHERE vendors.id='$id' 
                GROUP BY vendors.id")[0];
    }

    public static function grandTotals()
    {
        $totals = self::totals();
        $grand = 0;
        foreach ($totals as $total){
            $participants = ($total->plan =="A"?0:($total->plan=="B"?($total->participants<=1?0:$total->participants-1):$total->participants))*50;;
            $sem =  ($total->plan =="A"?($total->seminar_days<=2?0:$total->seminar_days-2):($total->plan=="B"?($total->seminar_days<=1?0:$total->seminar_days-1):$total->seminar_days))*500;
            $sem =  $sem<0?0:$sem;
            $grand += ($total->planCost + $total->brand_recognition + $total->promotional_flyer + $sem + $participants);
        }
        return $grand;
    }

    public static function byCity($id,$no = false)
    {
        if($no){
            return DataManager::rawQuery("SELECT COUNT(company.id) AS count
                FROM company 
                WHERE company.city=".$id)[0]->count - self::byCity($id);
        }
        return DataManager::rawQuery("SELECT COUNT(company.id) AS count
                FROM company 
                JOIN registration ON registration.company_id=company.id 
                WHERE company.city=".$id)[0]->count;
    }
