    public static function addUser($username,$email,$password,$user_group)
    {
        require '../mailer/Exception.php';
        require '../mailer/PHPMailer.php';
        require '../mailer/SMTP.php';
        $mail = new PHPMailer(true);
        $from = 'info@atlasvirtualshow.com';
        $mail->isHTML(true);                                   // Set email format to HTML
        $mail->Subject = "Admin Account Creation";
        $mail->Body    = "<p>An account was created for you on Atlas Dealers Online</p><p><h3>Please visit </h3><a href='https://atlasvirtualshow.com/dealers/admin'>https://atlasvirtualshow.com/dealers/admin</a> and login with the details below</p>".
            "<ul>
            <li><strong>Username:</strong>$username</li>
            <li><strong>Password:</strong>$password</li>
        </ul>";
        $mail->addAddress($email, $username);
        $mail->setFrom($from, 'Atlas Admin');

        $user = DataManager::insert("users",["username","email","password",'user_group'],[$username,$email,md5($password),$user_group]);

        if($user){
            $mail->send();
        }
    }
